import "mocha";
import { expect } from "chai";
import { AES256 } from "../../lib/aes256";

const ENCRYPTION_KEY = `abfb7c8d48dfc4f1ce7ed92a44989f25`; // (Length of 32)
const ENCRYPTION_IV = `232fcfdc2e2b4767`; // (Length of 16)

describe("AES256 Encryption and Decryption", () => {
  it("should encrypt the input string to expected output with provided key", () => {
    let aes = new AES256(ENCRYPTION_KEY, ENCRYPTION_IV);
    const INPUT_STRING = `merchantCode=MERCHANT123|reservationId=100000274061812|txnAmount=458.00|currencyType=INR|appCode=ET|pymtMode=Internet|txnDate=20150717|securityId=CRIS|RU=http://localhost:7001/eticketing/BankResponse|checkSum=35FCE86D084D7ABAFFFE1C71CDD310B7EFF935A5CC1D5F43890036906F25A129`;
    const ENCRYPTED_STRING = ``;

    let output = aes.encrypt(INPUT_STRING);

    expect(output).equal(ENCRYPTED_STRING);
  });

  it("should calculate the correct checksum of the provided input", () => {
    let output = AES256.checksum(
      `merchantCode=MERCHANT123|reservationId=100000274061812|txnAmount=458.00|currencyType=INR|appCode=ET|pymtMode=Internet|txnDate=20150717|securityId=CRIS|RU=http://localhost:7001/eticketing/BankResponse`
    );

    expect(output).equal(
      `35FCE86D084D7ABAFFFE1C71CDD310B7EFF935A5CC1D5F43890036906F25A129`
    );
  });

  it("should decrypt the input string to expected output with provided key", () => {
    let aes = new AES256(ENCRYPTION_KEY, ENCRYPTION_IV);
    const ENCRYPTED_INPUT_STRING = ``;
    const DECRYPTED_STRING = `merchantCode=MERCHANT123|reservationId=100000274061812|bankTxnId=IG01439494|txnAmount=458.00|status=Success|statusDesc=Completed successfully|checkSum=9550023F513ED8DC0B01D6DCABF2A8892F498B8E0187C6816AD9FBB3B747A5BD`;

    let output = aes.decrypt(ENCRYPTED_INPUT_STRING);

    expect(output).equal(DECRYPTED_STRING);
  });
});
